package TD44_Buoi5;

public class BT_InSoNguyenDuong {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<100;i++) {
			if(i%2==0) {
				System.out.print(" Số nguyên dương chẵn: " + i);
			}else {
				System.out.println();
				System.out.print("Số nguyên dương lẻ: " + i);
			}
		}

	}

}
